import { createStore } from 'vuex';

export default createStore({
  state: {
    cart: [], // Aquí se almacenarán los productos del carrito
  },
  mutations: {
    // Las mutaciones son funciones que modifican el estado
    addToCart(state, product) {
      state.cart.push(product);
    },
    // Otras mutaciones para eliminar productos, actualizar cantidades, etc.
  },
  actions: {
    // Las acciones son funciones que llaman a las mutaciones
    addToCart(context, product) {
      context.commit('addToCart', product);
    },
    // Otras acciones para eliminar productos, actualizar cantidades, etc.
  },
  getters: {
    // Los getters son funciones que devuelven parte del estado
    cartItems(state) {
      return state.cart;
    },
    // Obtener la cantidad total de productos en el carrito
    totalQuantity(state) {
      return state.cart.reduce((total, item) => total + item.quantity, 0);
    },
    // Obtener el subtotal del carrito
    subtotal(state) {
      return state.cart.reduce((total, item) => total + (item.price * item.quantity), 0);
    },
    // Otros getters para obtener la cantidad total de productos en el carrito, el subtotal, etc.
  },
});
