import { createApp } from 'vue';
import App from './App.vue';
import router from './router';
import store from './store';
import 'bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/vue-fontawesome';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faCartArrowDown } from '@fortawesome/free-solid-svg-icons';
import { createToast } from 'vue3-toastify'; // Importa la función createToast
import 'vue3-toastify/dist/index.css'; // Importa los estilos CSS de vue3-toastify

window.__VUE_PROD_HYDRATION_MISMATCH_DETAILS__ = false;

library.add(faCartArrowDown);

const app = createApp(App); // Crea la aplicación Vue
app.use(store); // Usa el store Vuex
app.use(router); // Usa el enrutador Vue Router
app.component('font-awesome-icon', FontAwesomeIcon); // Registra el componente FontAwesomeIcon
app.config.globalProperties.$toast = createToast(); // Configura la función $toast en el objeto global app
app.mount('#app'); // Monta la aplicación en el DOM
