import { createApp } from 'vue';
import App from './App.vue';
import router from './router';
import store from './store';
import 'bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/vue-fontawesome';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faCartArrowDown } from '@fortawesome/free-solid-svg-icons';
import { createToast } from 'vue3-toastify'; // Importa la funci贸n createToast
import 'vue3-toastify/dist/index.css'; // Importa los estilos CSS de vue3-toastify

window.__VUE_PROD_HYDRATION_MISMATCH_DETAILS__ = false;

library.add(faCartArrowDown);

createApp(App)
  .use(store)
  .use(router)
  .component('font-awesome-icon', FontAwesomeIcon)
  .mount('#app');
  app.config.globalProperties.$toast = createToast(); // Configura la funci贸n $toast en el objeto global app
  app.mount('#app');
