import NavBar from '@/components/NavBar.vue';
import CartItem from '@/components/CartItem.vue';
import Swal from 'sweetalert2';
import { useStore } from 'vuex';
import { computed } from 'vue';
export default {
  __name: 'CartView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const store = useStore();
    const {
      cartItems
    } = store.state;
    const subtotal = computed(() => {
      return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
    });
    const total = computed(() => {
      return subtotal.value;
    });

    // Función para abrir el modal de pago
    const openPaymentModal = () => {
      Swal.fire({
        title: 'Seleccione método de pago',
        html: `
      <button class="swal2-confirm swal2-styled" id="debitButton">Pago con Débito</button>
      <button class="swal2-confirm swal2-styled" id="creditButton">Pago con Crédito</button>
    `,
        showConfirmButton: false,
        showCloseButton: true,
        allowOutsideClick: false
      });

      // Manejar eventos de clic en los botones dentro del modal de SweetAlert2
      document.getElementById('debitButton').addEventListener('click', () => {
        openCreditCardModal('debit');
        Swal.close(); // Cerrar el modal después de seleccionar el método de pago
      });
      document.getElementById('creditButton').addEventListener('click', () => {
        openCreditCardModal('credit');
        Swal.close(); // Cerrar el modal después de seleccionar el método de pago
      });
    };

    // Función para abrir el modal de datos de tarjeta de crédito
    const openCreditCardModal = () => {
      Swal.fire({
        title: 'Ingrese los datos de su tarjeta de crédito',
        html: `
      <!-- Aquí puedes agregar campos para capturar los datos de la tarjeta de crédito -->
      <input type="text" id="cardNumber" placeholder="Número de tarjeta">
      <input type="text" id="expiryDate" placeholder="Fecha de vencimiento">
      <input type="text" id="cvv" placeholder="CVV">
    `,
        showConfirmButton: true,
        showCloseButton: true,
        allowOutsideClick: false,
        preConfirm: () => {
          // Aquí puedes realizar la validación de los datos de la tarjeta de crédito y el proceso de pago
          const cardNumber = document.getElementById('cardNumber').value;
          const expiryDate = document.getElementById('expiryDate').value;
          const cvv = document.getElementById('cvv').value;
          console.log('Datos de la tarjeta de crédito:', {
            cardNumber,
            expiryDate,
            cvv
          });
        }
      });
    };
    const __returned__ = {
      store,
      cartItems,
      subtotal,
      total,
      openPaymentModal,
      openCreditCardModal,
      NavBar,
      CartItem,
      get Swal() {
        return Swal;
      },
      get useStore() {
        return useStore;
      },
      computed
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
