import { ref } from 'vue';

const state = {
  cartItems: ref([]) // Lista de productos en el carrito
};

const mutations = {
  // Agregar un producto al carrito

  // Eliminar un producto del carrito
  removeFromCart(state, productId) {
    state.cartItems = state.cartItems.filter(item => item.id !== productId);
  },
  // Actualizar la cantidad de un producto en el carrito
  updateQuantity(state, { productId, quantity }) {
    const productIndex = state.cartItems.findIndex(item => item.id === productId);
    if (productIndex !== -1) {
      state.cartItems[productIndex].quantity = quantity;
    }
  }
};

const actions = {
  // Acción para agregar un producto al carrito
  addToCart({ commit }, product) {
    commit('addToCart', product);
  },
  // Acción para eliminar un producto del carrito
  removeFromCart({ commit }, productId) {
    commit('removeFromCart', productId);
  },
  // Acción para actualizar la cantidad de un producto en el carrito
  updateQuantity({ commit }, { productId, quantity }) {
    commit('updateQuantity', { productId, quantity });
  }
};

const getters = {
  // Obtener todos los productos en el carrito
  cartItems(state) {
    return state.cartItems; // Devolver los productos del carrito
  },
  // Obtener la cantidad total de productos en el carrito
  totalQuantity: state => state.cartItems.reduce((total, item) => total + item.quantity, 0),
  // Obtener el subtotal del carrito
  subtotal: state => state.cartItems.reduce((total, item) => total + (item.price * item.quantity), 0)
};

export default {
  state,
  mutations,
  actions,
  getters,
};
